sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
	response.setStatus(200);

	var ticket_number = request.pathParams.ticket_number;

	response.setHeader("Access-Control-Allow-Origin","*");
	response.setHeader("Access-Control-Allow-Methods","GET, POST, PUT");
    response.setHeader("Access-Control-Allow-Headers","Content-Type");

	var corresTracking = new GlideRecord("x_1513386_hoa_correspondence_tracking");
	var caseMgmt = corresTracking.addJoinQuery("x_1513386_hoa_case_mgmt");
    corresTracking.addQuery("number", ticket_number);
    corresTracking.query();



    var records = [];
	var caseSysId = "initial";



    if (corresTracking.next()) {

			records.push("SYSID:"+caseSysId);
			records.push("caseNumber:"+caseMgmt.getValue('Number'));


	}

	response.setBody({

		"corres": {
			"record" : records
		},
		"case" : {
			"caseNumber" : ticket_number
		}
	});

})(request, response);
